import React, { useEffect, useState } from "react";
import "./App.css";

const App = () => {
  const [clicked, setClicked] = useState(false);
  const [exchangeRates, setExchangeData] = useState(null);
  const [currency, setCurrency] = useState("USD");

  useEffect(
    () => {
      let ignore = false;
      fetch(
        `https://v6.exchangerate-api.com/v6/ee28c875c5b6be2f4dbc3d5d/latest/${currency}`
      )
        .then((response) => response.json())
        .then((data) => {
          if (!ignore) {
            setExchangeData(Object.keys(data.conversion_rates));
          }
        });
      return () => {
        ignore = true;
      };
    },
    [currency]
  );

  const handleClick = () => {
    setClicked(!clicked);
  };

  return (
    <div className="App">
      <h1>Dwight Funding Assesment</h1>
      <label>
        Choose current currency
        <select onChange={(e) => setCurrency(e.target.value)}>
          <option value="Choose current currency">
            {" "}
            Choose current currency
          </option>
          {exchangeRates
            ? exchangeRates.map((cur) => <option value={cur}> {cur}</option>)
            : ""}
        </select>
      </label>
      <button onClick={() => handleClick()}>Click Me</button>
      {clicked ? <h1>Lets get some data!</h1> : ""}
    </div>
  );
};

export default App;
